import {
  Box,
  Button,
  Container,
  Dialog,
  Grid,
  makeStyles,
  Typography,
} from "@material-ui/core";
import React from "react";
import CustomButton from "../comman/CustomButton";
import { Swiper, SwiperSlide } from "swiper/react";
import SwiperCore, { EffectFade, Pagination, Scrollbar, A11y } from "swiper";
import "swiper/swiper.scss";
import "swiper/components/effect-fade/effect-fade.min.css";
import "swiper/components/pagination/pagination.min.css";
import sliderImage1 from "../../assets/images/sliderImage1.jpeg";
import sliderImage2 from "../../assets/images/sliderImage2.jpeg";
import sliderImage3 from "../../assets/images/sliderImage3.jpeg";
import videoImage from "../../assets/images/video-block.jpeg";
import { Link } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPlay } from "@fortawesome/free-solid-svg-icons";
import DialogContent from "@material-ui/core/DialogContent";
import HomeAbout from "./homeAbout";
import AboutFish from "./aboutFish";
import FishType from "./fishType";
import Gallery from "./gallery";
import Tickets from "./tickets";
import Donation from "./donation";
import Events from "./events";
import Instagram from "./instagram";
import Partners from "../comman/partners";
import Testimonial from "../comman/testimonial";
SwiperCore.use([Pagination, Scrollbar, A11y, EffectFade]);

const useStyles = makeStyles((theme) => ({
  bannerSection: {
    position: "relative",
    [theme.breakpoints.down("768")]: {
      height: 300,
    },
  },
  bannerSwiper: {
    height: "100%",
    "& img": {
      width: "100%",
      height: "100%",
      objectFit: "cover",
    },
  },
  bannerText: {
    padding: "300px 0",
    "& h3": {
      color: theme.palette.background.paper,
      fontSize: 60,
      lineHeight: "60px",
      fontWeight: 300,
      letterSpacing: -3,
      fontFamily: '"Quicksand", sans-serif',
    },
    "& h1": {
      color: theme.palette.background.paper,
      fontSize: 100,
      fontWeight: 700,
      lineHeight: "100px",
      fontFamily: '"Quicksand", sans-serif',
    },
    "& h6": {
      color: theme.palette.background.paper,
      marginBottom: 50,
      fontWeight: 600,
      maxWidth: 1000,
    },
  },
  videoBox: {
    position: "relative",
  },
  videoImage: {
    width: 292,
    position: "relative",
    overflow: "hidden",
    "&::after": {
      position: "absolute",
      content: '""',
      top: 0,
      right: 0,
      left: 0,
      bottom: 0,
      backgroundColor: "rgba(0,0,0,0.3)",
    },
    "& img": {
      width: "100%",
      height: "100%",
      objectFit: "contain",
      transition: "all 0.8s ease-in-out",
      transform: "scale(1)",
    },
    "&:hover img": {
      transform: "scale(1.2)",
    },
  },
  videoContent: {
    position: "absolute",
    top: 0,
    right: 0,
    bottom: 0,
    left: 0,
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    flexDirection: "column",
    "& h6": {
      color: theme.palette.background.paper,
      fontSize: 18,
      marginTop: 8,
    },
  },
  videoDialog: {
    "& .MuiDialogContent-root": {
      padding: 0,
    },
  },
  videoBtn: {
    width: "50px",
    minWidth: "50px",
    height: "50px",
    borderRadius: "60px",
    backgroundColor: "#000",
    "&:hover": {
      backgroundColor: theme.palette.primary.main,
    },
    "& svg": {
      color: theme.palette.background.paper,
      fontSize: "13px",
      margin: "8px 0 0 3px",
    },
  },
  rightBoxWrap: {
    alignItems: "center",
    height: "155px",
    backgroundColor: theme.palette.background.paper,
  },
  eventsTitle: {
    position: "relative",
    "& h6": {
      color: "#dcdcdc",
      fontSize: "24px",
      fontWeight: 700,
      display: "inline-block",
      transform: "translateY(20%) translateX(40%) rotate(-90deg)",
    },
